import "./nav.css"
import { useState } from "react"

const Nav=()=>{
  const [clName,setclName] = useState("show")
  const [navcl,setNavcl] = useState("hide")
  const hide=()=>{
    setclName("hide")
    setNavcl("show")
  }
    return(
        <>
        <div class={`open-navbar-icon navbar-icon center ${clName}`} onClick={hide}>
        <div class="line"></div>
        <div class="line"></div>
        <div class="line"></div>
      </div>
      <div class={`open-navbar-icon navbar-icon center ${clName}`} onClick={hide}>
        <div class="line"></div>
        <div class="line"></div>
        <div class="line"></div>
      </div>
      <div class={`open-navbar-icon navbar-icon center ${clName}`} onClick={hide}>
        <div class="line"></div>
        <div class="line"></div>
        <div class="line"></div>
      </div>
      </>
    )
}
export default Nav;
