import SideDrawer from "../sideDrawer"
import "./nav.css"
import { useState } from "react"

const Nav=()=>{
  const [clName,setclName] = useState("show")
  const [navcl,setNavcl] = useState("hide")
  const hide=()=>{
    setclName("hide")
    setNavcl("show")
  }
    return(
        <>
        <div className="navy">
       <SideDrawer onClick={hide} getclass={navcl}></SideDrawer>
     <div>
      <h2 className="logo">Horeca</h2>
     </div>
     <div>
      <a className="git" href="https://github.com/omkarjninja">Github</a>
     </div>
        </div>
       
      </>
    )
}
export default Nav;
